contracts/Leadrate.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './interface/IERC20.sol';
import './interface/IOracleFreeDollar.sol';
import './interface/IReserve.sol';

/**
 * @title Leadrate (attempt at translating the nicely concise German term 'Leitzins')
 *
 * A module that can provide other modules with the lead interest rate for the system.
 *
 **/
contract Leadrate {

	IReserve public immutable equity;

	// the following five variables are less than 256 bit so they should be stored
	// in the same slot, making them cheap to access together, right?

	uint24 public currentRatePPM; // 24 bit allows rates of up to 1670% per year
	uint24 public nextRatePPM;
	uint40 public nextChange;

	uint40 private anchorTime; // 40 bits for time in seconds spans up to 1000 human generations
	uint64 private ticksAnchor; // in bips * seconds, uint88 allows up to

	event RateProposed(address who, uint24 nextRate, uint40 nextChange);
	event RateChanged(uint24 newRate);

	error NoPendingChange();
	error ChangeNotReady();

	constructor(IReserve equity_, uint24 initialRatePPM) {
		equity = equity_;
		nextRatePPM = initialRatePPM;
		currentRatePPM = initialRatePPM;
		nextChange = uint40(block.timestamp);
		anchorTime = nextChange;
		ticksAnchor = 0;
		emit RateChanged(initialRatePPM); // emit for init indexing, if wanted
	}

	/**
     * Proposes a new interest rate that will automatically be applied after seven days.
     * To cancel a proposal, just overwrite it with a new one proposing the current rate.
     */
	function proposeChange(uint24 newRatePPM_, address[] calldata helpers) external {
		equity.checkQualified(msg.sender, helpers);
		nextRatePPM = newRatePPM_;
		nextChange = uint40(block.timestamp + 7 days);
		emit RateProposed(msg.sender, nextRatePPM, nextChange);
	}

	/**
     * Setting a previously proposed interest rate change into force.
     */
	function applyChange() external {
		if (currentRatePPM == nextRatePPM) revert NoPendingChange();
		uint40 timeNow = uint40(block.timestamp);
		if (timeNow < nextChange) revert ChangeNotReady();
		ticksAnchor += (timeNow - anchorTime) * currentRatePPM;
		anchorTime = timeNow;
		currentRatePPM = nextRatePPM;
		emit RateChanged(currentRatePPM);
	}

	/**
     * Total accumulated 'interest ticks' since this contract was deployed.
     * One 'tick' is a ppm-second, so one months of 12% annual interest is
     *   120000*30*24*3600 = 311040000000 ticks.
     * Two months of 6% annual interest would result in the same number of
     * ticks. For simplicity, this is linear, so there is no "interest on interest".
     */
	function currentTicks() public view returns (uint64) {
		return ticks(block.timestamp);
	}

	function ticks(uint256 timestamp) public view returns (uint64) {
		return ticksAnchor + (uint64(timestamp) - anchorTime) * currentRatePPM;
	}

}

contracts/MintingHub.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './interface/IERC20.sol';
import './interface/ILeadrate.sol';
import './interface/IOracleFreeDollar.sol';
import './interface/IPosition.sol';
import './interface/IPositionFactory.sol';
import './interface/IReserve.sol';

import './PositionRoller.sol';
import './utils/Ownable.sol';

/**
 * @title Minting Hub
 * @notice The central hub for creating, cloning and challenging collateralized OracleFreeDollar positions.
 * @dev Only one instance of this contract is required, whereas every new position comes with a new position
 * contract. Pending challenges are stored as structs in an array.
 */
contract MintingHub {
	/**
     * @notice Irrevocable fee in OFD when proposing a new position (but not when cloning an existing one).
     */
	uint256 public constant OPENING_FEE = 1000 * 10 ** 18;

	/**
     * @notice The challenger reward in parts per million (ppm) relative to the challenged amount, whereas
     * challenged amount if defined as the challenged collateral amount times the liquidation price.
     */
	uint256 public constant CHALLENGER_REWARD = 20000; // 2%
	uint256 public constant EXPIRED_PRICE_FACTOR = 10;

	IPositionFactory private immutable POSITION_FACTORY; // position contract to clone

	IOracleFreeDollar public immutable ofd; // currency
	PositionRoller public immutable roller; // helper to roll positions
	ILeadrate public immutable rate; // to determine the interest rate

	Challenge[] public challenges; // list of open challenges

	/**
     * @notice Map to remember pending postponed collateral returns.
     * @dev It maps collateral => beneficiary => amount.
     */
	mapping(address collateral => mapping(address owner => uint256 amount)) public pendingReturns;

	struct Challenge {
		address challenger; // the address from which the challenge was initiated
		uint40 start; // the start of the challenge
		IPosition position; // the position that was challenged
		uint256 size; // how much collateral the challenger provided
	}

	event PositionOpened(address indexed owner, address indexed position, address original, address collateral);
	event ChallengeStarted(address indexed challenger, address indexed position, uint256 size, uint256 number);
	event ChallengeAverted(address indexed position, uint256 number, uint256 size);
	event ChallengeSucceeded(
		address indexed position,
		uint256 number,
		uint256 bid,
		uint256 acquiredCollateral,
		uint256 challengeSize
	);
	event PostPonedReturn(address collateral, address indexed beneficiary, uint256 amount);
	event ForcedSale(address pos, uint256 amount, uint256 priceE36MinusDecimals);

	error UnexpectedPrice();
	error InvalidPos();
	error IncompatibleCollateral();
	error InsufficientCollateral();

	modifier validPos(address position) {
		if (ofd.getPositionParent(position) != address(this)) revert InvalidPos();
		_;
	}

	constructor(address _ofd, address _leadrate, address _roller, address _factory) {
		ofd = IOracleFreeDollar(_ofd);
		rate = ILeadrate(_leadrate);
		POSITION_FACTORY = IPositionFactory(_factory);
		roller = PositionRoller(_roller);
	}

	/**
     * @notice Open a collateralized loan position. See also https://oracle-free-dollar.gitbook.io/ofd/collateralised-minting/opening-new-position .
     * @dev For a successful call, you must set an allowance for the collateral token, allowing
     * the minting hub to transfer the initial collateral amount to the newly created position and to
     * withdraw the fees.
     *
     * @param _collateralAddress        address of collateral token
     * @param _minCollateral     minimum collateral required to prevent dust amounts
     * @param _initialCollateral amount of initial collateral to be deposited
     * @param _mintingMaximum    maximal amount of OFD that can be minted by the position owner
     * @param _expirationSeconds position tenor in unit of timestamp (seconds) from 'now'
     * @param _challengeSeconds  challenge period. Longer for less liquid collateral.
     * @param _riskPremium       ppm of minted amount that is added to the applicible minting fee as a risk premium
     * @param _liqPrice          Liquidation price with (36 - token decimals) decimals,
     *                           e.g. 18 decimals for an 18 dec collateral, 36 decs for a 0 dec collateral.
     * @param _reservePPM        ppm of minted amount that is locked as borrower's reserve, e.g. 20%
     * @return address           address of created position
     */
	function openPosition(
		address _collateralAddress,
		uint256 _minCollateral,
		uint256 _initialCollateral,
		uint256 _mintingMaximum,
		uint40 _initPeriodSeconds,
		uint40 _expirationSeconds,
		uint40 _challengeSeconds,
		uint24 _riskPremium,
		uint256 _liqPrice,
		uint24 _reservePPM
	) public returns (address) {
		{
			require(_riskPremium <= 1000000);
			require(CHALLENGER_REWARD <= _reservePPM && _reservePPM <= 1000000);
			require(IERC20(_collateralAddress).decimals() <= 24); // leaves 12 digits for price
			uint256 invalidAmount = IERC20(_collateralAddress).totalSupply() + 1;
			try IERC20(_collateralAddress).transfer(address(0x123), invalidAmount) {
				revert IncompatibleCollateral(); // we need a collateral that reverts on failed transfers
			} catch Error(string memory /*reason*/) {
			} catch Panic(uint /*errorCode*/) {
			} catch (bytes memory /*lowLevelData*/) {
			}
			if (_initialCollateral < _minCollateral) revert InsufficientCollateral();
			if (_minCollateral * _liqPrice < 3500 ether * 10 ** 18) revert InsufficientCollateral(); // must start with at least 3500 OFD worth of collateral
		}
		IPosition pos = IPosition(
			POSITION_FACTORY.createNewPosition(
				msg.sender,
				address(ofd),
				_collateralAddress,
				_minCollateral,
				_mintingMaximum,
				_initPeriodSeconds,
				_expirationSeconds,
				_challengeSeconds,
				_riskPremium,
				_liqPrice,
				_reservePPM
			)
		);
		ofd.registerPosition(address(pos));
		ofd.collectProfits(msg.sender, OPENING_FEE);
		IERC20(_collateralAddress).transferFrom(msg.sender, address(pos), _initialCollateral);

		emit PositionOpened(msg.sender, address(pos), address(pos), _collateralAddress);
		return address(pos);
	}

	function clone(address parent, uint256 _initialCollateral, uint256 _initialMint, uint40 expiration) public returns (address) {
		return clone(msg.sender, parent, _initialCollateral, _initialMint, expiration);
	}

	/**
     * @notice Clones an existing position and immediately tries to mint the specified amount using the given collateral.
     * @dev This needs an allowance to be set on the collateral contract such that the minting hub can get the collateral.
     */
	function clone(address owner, address parent, uint256 _initialCollateral, uint256 _initialMint, uint40 expiration) public validPos(parent) returns (address) {
		address pos = POSITION_FACTORY.clonePosition(parent);
		IPosition child = IPosition(pos);
		child.initialize(parent, expiration);
		ofd.registerPosition(pos);
		IERC20 collateral = child.collateral();
		if (_initialCollateral < child.minimumCollateral()) revert InsufficientCollateral();
		collateral.transferFrom(msg.sender, pos, _initialCollateral); // collateral must still come from sender for security
		emit PositionOpened(owner, address(pos), parent, address(collateral));
		child.mint(owner, _initialMint);
		Ownable(address(child)).transferOwnership(owner);
		return address(pos);
	}

	/**
     * @notice Launch a challenge (Dutch auction) on a position
     * @param _positionAddr      address of the position we want to challenge
     * @param _collateralAmount  amount of the collateral we want to challenge
     * @param minimumPrice       position.price() to guard against the minter fruntrunning with a price change
     * @return index of the challenge in challenge-array
     */
	function challenge(
		address _positionAddr,
		uint256 _collateralAmount,
		uint256 minimumPrice
	) external validPos(_positionAddr) returns (uint256) {
		IPosition position = IPosition(_positionAddr);
		// challenger should be ok if frontrun by owner with a higher price
		// in case owner fruntruns challenger with small price decrease to prevent challenge,
		// the challenger should set minimumPrice to market price
		if (position.price() < minimumPrice) revert UnexpectedPrice();
		IERC20(position.collateral()).transferFrom(msg.sender, address(this), _collateralAmount);
		uint256 pos = challenges.length;
		challenges.push(Challenge(msg.sender, uint40(block.timestamp), position, _collateralAmount));
		position.notifyChallengeStarted(_collateralAmount);
		emit ChallengeStarted(msg.sender, address(position), _collateralAmount, pos);
		return pos;
	}

	/**
     * @notice Post a bid in OFD given an open challenge.
     *
     * @dev In case that the collateral cannot be transfered back to the challenger (i.e. because the collateral token
     * has a blacklist and the challenger is on it), it is possible to postpone the return of the collateral.
     *
     * @param _challengeNumber  index of the challenge as broadcast in the event
     * @param size              how much of the collateral the caller wants to bid for at most
     *                          (automatically reduced to the available amount)
     * @param postponeCollateralReturn To postpone the return of the collateral to the challenger. Usually false.
     */
	function bid(uint32 _challengeNumber, uint256 size, bool postponeCollateralReturn) external {
		Challenge memory _challenge = challenges[_challengeNumber];
		(uint256 liqPrice, uint40 phase) = _challenge.position.challengeData();
		size = _challenge.size < size ? _challenge.size : size; // cannot bid for more than the size of the challenge

		if (block.timestamp <= _challenge.start + phase) {
			_avertChallenge(_challenge, _challengeNumber, liqPrice, size);
			emit ChallengeAverted(address(_challenge.position), _challengeNumber, size);
		} else {
			_returnChallengerCollateral(_challenge, _challengeNumber, size, postponeCollateralReturn);
			(uint256 transferredCollateral, uint256 offer) = _finishChallenge(_challenge, liqPrice, phase, size);
			emit ChallengeSucceeded(address(_challenge.position), _challengeNumber, offer, transferredCollateral, size);
		}
	}

	function _finishChallenge(Challenge memory _challenge, uint256 liqPrice, uint40 phase, uint256 size) internal returns (uint256, uint256) {
		// Repayments depend on what was actually minted, whereas bids depend on the available collateral
		(address owner, uint256 collateral, uint256 repayment, uint32 reservePPM) = _challenge.position.notifyChallengeSucceeded(msg.sender, size);

		// No overflow possible thanks to invariant (col * price <= limit * 10**18)
		// enforced in Position.setPrice and knowing that collateral <= col.
		uint256 offer = (_calculatePrice(_challenge.start + phase, phase, liqPrice) * collateral) / 10 ** 18;
		ofd.transferFrom(msg.sender, address(this), offer); // get money from bidder
		uint256 reward = (offer * CHALLENGER_REWARD) / 1000_000;
		ofd.transfer(_challenge.challenger, reward); // pay out the challenger reward
		uint256 fundsAvailable = offer - reward; // funds available after reward

		// Example: available funds are 90, repayment is 50, reserve 20%. Then 20%*(90-50)=16 are collected as profits
		// and the remaining 34 are sent to the position owner. If the position owner maxed out debt before the challenge
		// started and the liquidation price was 100, they would be slightly better off as they would get away with 80
		// instead of 40+36 = 76 in this example.
		if (fundsAvailable > repayment) {
			// The excess amount is distributed between the system and the owner using the reserve ratio
			// At this point, we cannot rely on the liquidation price because the challenge might have been started as a
			// response to an unreasonable increase of the liquidation price, such that we have to use this heuristic
			// for excess fund distribution, which make position owners that maxed out their positions slightly better
			// off in comparison to those who did not.
			uint256 profits = (reservePPM * (fundsAvailable - repayment)) / 1000_000;
			ofd.collectProfits(address(this), profits);
			ofd.transfer(owner, fundsAvailable - repayment - profits);
		} else if (fundsAvailable < repayment) {
			ofd.coverLoss(address(this), repayment - fundsAvailable); // ensure we have enough to pay everything
		}
		ofd.burnWithoutReserve(repayment, reservePPM); // Repay the challenged part, example: 50 OFD leading to 10 OFD in implicit profits
		return (collateral, offer);
	}

	function _avertChallenge(Challenge memory _challenge, uint32 number, uint256 liqPrice, uint256 size) internal {
		require(block.timestamp != _challenge.start); // do not allow to avert the challenge in the same transaction
		if (msg.sender == _challenge.challenger) {
			// allow challenger to cancel challenge without paying themselves
		} else {
			ofd.transferFrom(msg.sender, _challenge.challenger, (size * liqPrice) / (10 ** 18));
		}

		_challenge.position.notifyChallengeAverted(size);
		_challenge.position.collateral().transfer(msg.sender, size);
		if (size < _challenge.size) {
			challenges[number].size = _challenge.size - size;
		} else {
			require(size == _challenge.size);
			delete challenges[number];
		}
	}

	/**
     * @notice Returns 'amount' of the collateral to the challenger and reduces or deletes the relevant challenge.
     */
	function _returnChallengerCollateral(
		Challenge memory _challenge,
		uint32 number,
		uint256 amount,
		bool postpone
	) internal {
		_returnCollateral(_challenge.position.collateral(), _challenge.challenger, amount, postpone);
		if (_challenge.size == amount) {
			// bid on full amount
			delete challenges[number];
		} else {
			// bid on partial amount
			challenges[number].size -= amount;
		}
	}

	/**
     * @notice Calculates the current Dutch auction price.
     * @dev Starts at the full price at time 'start' and linearly goes to 0 as 'phase2' passes.
     */
	function _calculatePrice(uint40 start, uint40 phase2, uint256 liqPrice) internal view returns (uint256) {
		uint40 timeNow = uint40(block.timestamp);
		if (timeNow <= start) {
			return liqPrice;
		} else if (timeNow >= start + phase2) {
			return 0;
		} else {
			uint256 timeLeft = phase2 - (timeNow - start);
			return (liqPrice / phase2) * timeLeft;
		}
	}

	/**
     * @notice Get the price per unit of the collateral for the given challenge.
     * @dev The price comes with (36-collateral.decimals()) digits, such that multiplying it with the
     * raw collateral amount always yields a price with 36 digits, or 18 digits after dividing by 10**18 again.
     */
	function price(uint32 challengeNumber) public view returns (uint256) {
		Challenge memory _challenge = challenges[challengeNumber];
		if (_challenge.challenger == address(0x0)) {
			return 0;
		} else {
			(uint256 liqPrice, uint40 phase) = _challenge.position.challengeData();
			return _calculatePrice(_challenge.start + phase, phase, liqPrice);
		}
	}

	/**
     * @notice Challengers can call this method to withdraw collateral whose return was postponed.
     */
	function returnPostponedCollateral(address collateral, address target) external {
		uint256 amount = pendingReturns[collateral][msg.sender];
		delete pendingReturns[collateral][msg.sender];
		IERC20(collateral).transfer(target, amount);
	}

	function _returnCollateral(IERC20 collateral, address recipient, uint256 amount, bool postpone) internal {
		if (postpone) {
			// Postponing helps in case the challenger was blacklisted or otherwise cannot receive at the moment.
			pendingReturns[address(collateral)][recipient] += amount;
			emit PostPonedReturn(address(collateral), recipient, amount);
		} else {
			collateral.transfer(recipient, amount); // return the challenger's collateral
		}
	}

	/**
     * The applicable purchase price when forcing the sale of collateral of an expired position.
     *
     * The price starts at 10x the liquidation price at the expiration time, linearly declines to
     * 1x liquidation price over the course of one challenge period, and then linearly declines
     * less steeply to 0 over the course of another challenge period.
     */
	function expiredPurchasePrice(IPosition pos) public view returns (uint256) {
		uint256 liqprice = pos.price();
		uint256 expiration = pos.expiration();
		if (block.timestamp <= expiration) {
			return EXPIRED_PRICE_FACTOR * liqprice;
		} else {
			uint256 challengePeriod = pos.challengePeriod();
			uint256 timePassed = block.timestamp - expiration;
			if (timePassed <= challengePeriod) {
				// from 10x liquidation price to 1x in first phase
				uint256 timeLeft = challengePeriod - timePassed;
				return liqprice + (((EXPIRED_PRICE_FACTOR - 1) * liqprice) / challengePeriod) * timeLeft;
			} else if (timePassed < 2 * challengePeriod) {
				// from 1x liquidation price to 0 in second phase
				uint256 timeLeft = 2 * challengePeriod - timePassed;
				return (liqprice / challengePeriod) * timeLeft;
			} else {
				// get collateral for free after both phases passed
				return 0;
			}
		}
	}

	/**
     * Buys up to the desired amount of the collateral asset from the given expired position using
     * the applicable 'expiredPurchasePrice' in that instant.
     */
	function buyExpiredCollateral(IPosition pos, uint256 upToAmount) external returns (uint256) {
		uint256 max = pos.collateral().balanceOf(address(pos));
		uint256 amount = upToAmount > max ? max : upToAmount;
		uint256 forceSalePrice = expiredPurchasePrice(pos);
		uint256 costs = (forceSalePrice * amount) / 10 ** 18;
		pos.forceSale(msg.sender, amount, costs);
		emit ForcedSale(address(pos), amount, forceSalePrice);
		return amount;
	}
}

contracts/OracleFreeDollar.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./utils/ERC20PermitLight.sol";
import "./Equity.sol";
import "./interface/IReserve.sol";
import "./interface/IOracleFreeDollar.sol";

/**
 * @title OracleFreeDollar
 * @notice The OracleFreeDollar (OFD) is an ERC-20 token that is designed to track the value of the American Dollar.
 * It is not upgradable, but open to arbitrary minting plugins. These are automatically accepted if none of the
 * qualified pool share holders casts a veto, leading to a flexible but conservative governance.
 */
contract OracleFreeDollar is ERC20PermitLight, IOracleFreeDollar {
    /**
     * @notice Minimal fee and application period when suggesting a new minter.
     */
    uint256 public constant MIN_FEE = 1000 * (10 ** 18);
    uint256 public immutable MIN_APPLICATION_PERIOD; // for example 10 days

    /**
     * @notice The contract that holds the reserve.
     */
    IReserve public immutable override reserve;

    /**
     * @notice How much of the reserve belongs to the minters. Everything else belongs to the pool share holders.
     * Stored with 6 additional digits of accuracy so no rounding is necessary when dealing with parts per
     * million (ppm) in reserve calculations.
     */
    uint256 private minterReserveE6;

    /**
     * @notice Map of minters to approval time stamps. If the time stamp is in the past, the minter contract is allowed
     * to mint OracleFreeDollars.
     */
    mapping(address minter => uint256 validityStart) public minters;

    /**
     * @notice List of positions that are allowed to mint and the minter that registered them.
     */
    mapping(address position => address registeringMinter) public positions;

    event MinterApplied(address indexed minter, uint256 applicationPeriod, uint256 applicationFee, string message);
    event MinterDenied(address indexed minter, string message);
    event Loss(address indexed reportingMinter, uint256 amount);
    event Profit(address indexed reportingMinter, uint256 amount);

    error PeriodTooShort();
    error FeeTooLow();
    error AlreadyRegistered();
    error NotMinter();
    error TooLate();

    modifier minterOnly() {
        if (!isMinter(msg.sender) && !isMinter(positions[msg.sender])) revert NotMinter();
        _;
    }

    /**
     * @notice Initiates the OracleFreeDollar with the provided minimum application period for new plugins
     * in seconds, for example 10 days, i.e. 3600*24*10 = 864000
     */
    constructor(uint256 _minApplicationPeriod) ERC20(18) {
        MIN_APPLICATION_PERIOD = _minApplicationPeriod;
        reserve = new Equity(this);
    }

    function name() external pure override returns (string memory) {
        return "OracleFreeDollar";
    }

    function symbol() external pure override returns (string memory) {
        return "OFD";
    }

    function initialize(address _minter, string calldata _message) external {
        require(totalSupply() == 0 && reserve.totalSupply() == 0);
        minters[_minter] = block.timestamp;
        emit MinterApplied(_minter, 0, 0, _message);
    }

    /**
     * @notice Publicly accessible method to suggest a new way of minting OracleFreeDollar.
     * @dev The caller has to pay an application fee that is irrevocably lost even if the new minter is vetoed.
     * The caller must assume that someone will veto the new minter unless there is broad consensus that the new minter
     * adds value to the OracleFreeDollar system. Complex proposals should have application periods and applications fees
     * above the minimum. It is assumed that over time, informal ways to coordinate on new minters emerge. The message
     * parameter might be useful for initiating further communication. Maybe it contains a link to a website describing
     * the proposed minter.
     *
     * @param _minter              An address that is given the permission to mint OracleFreeDollars
     * @param _applicationPeriod   The time others have to veto the suggestion, at least MIN_APPLICATION_PERIOD
     * @param _applicationFee      The fee paid by the caller, at least MIN_FEE
     * @param _message             An optional human readable message to everyone watching this contract
     */
    function suggestMinter(
        address _minter,
        uint256 _applicationPeriod,
        uint256 _applicationFee,
        string calldata _message
    ) external override {
        if (_applicationPeriod < MIN_APPLICATION_PERIOD) revert PeriodTooShort();
        if (_applicationFee < MIN_FEE) revert FeeTooLow();
        if (minters[_minter] != 0) revert AlreadyRegistered();
        _collectProfits(address(this), msg.sender, _applicationFee);
        minters[_minter] = block.timestamp + _applicationPeriod;
        emit MinterApplied(_minter, _applicationPeriod, _applicationFee, _message);
    }

    /**
     * @notice Make the system more user friendly by skipping the allowance in many cases.
     * @dev We trust minters and the positions they have created to mint and burn as they please, so
     * giving them arbitrary allowances does not pose an additional risk.
     */
    function _allowance(address owner, address spender) internal view override returns (uint256) {
        uint256 explicit = super._allowance(owner, spender);
        if (explicit > 0) {
            return explicit; // don't waste gas checking minter
        } else if (isMinter(spender) || isMinter(getPositionParent(spender)) || spender == address(reserve)) {
            return INFINITY;
        } else {
            return 0;
        }
    }

    /**
     * @notice The reserve provided by the owners of collateralized positions.
     * @dev The minter reserve can be used to cover losses after the equity holders have been wiped out.
     */
    function minterReserve() public view returns (uint256) {
        return minterReserveE6 / 1000000;
    }

    /**
     * @notice Allows minters to register collateralized debt positions, thereby giving them the ability to mint OracleFreeDollars.
     * @dev It is assumed that the responsible minter that registers the position ensures that the position can be trusted.
     */
    function registerPosition(address _position) external override {
        if (!isMinter(msg.sender)) revert NotMinter();
        positions[_position] = msg.sender;
    }

    /**
     * @notice The amount of equity of the OracleFreeDollar system in OFD, owned by the holders of OracleFreeDollar Pool Shares.
     * @dev Note that the equity contract technically holds both the minter reserve as well as the equity, so the minter
     * reserve must be subtracted. All fees and other kind of income is added to the Equity contract and essentially
     * constitutes profits attributable to the pool share holders.
     */
    function equity() public view returns (uint256) {
        uint256 balance = balanceOf(address(reserve));
        uint256 minReserve = minterReserve();
        if (balance <= minReserve) {
            return 0;
        } else {
            return balance - minReserve;
        }
    }

    /**
     * @notice Qualified pool share holders can deny minters during the application period.
     * @dev Calling this function is relatively cheap thanks to the deletion of a storage slot.
     */
    function denyMinter(address _minter, address[] calldata _helpers, string calldata _message) external override {
        if (block.timestamp > minters[_minter]) revert TooLate();
        reserve.checkQualified(msg.sender, _helpers);
        delete minters[_minter];
        emit MinterDenied(_minter, _message);
    }

    /**
     * @notice Mints the provided amount of OFD to the target address, automatically forwarding
     * the minting fee and the reserve to the right place.
     */
    function mintWithReserve(
        address _target,
        uint256 _amount,
        uint32 _reservePPM,
        uint32 _feesPPM
    ) external override minterOnly {
        uint256 usableMint = (_amount * (1000_000 - _feesPPM - _reservePPM)) / 1000_000; // rounding down is fine
        _mint(_target, usableMint);
        _mint(address(reserve), _amount - usableMint); // rest goes to equity as reserves or as fees
        minterReserveE6 += _amount * _reservePPM;
        emit Profit(msg.sender, (_feesPPM * _amount) / 1000_000);
    }

    function mint(address _target, uint256 _amount) external override minterOnly {
        _mint(_target, _amount);
    }

    /**
     * Anyone is allowed to burn their OFD.
     */
    function burn(uint256 _amount) external {
        _burn(msg.sender, _amount);
    }

    /**
     * @notice Burn someone elses OFD.
     */
    function burnFrom(address _owner, uint256 _amount) external override minterOnly {
        _burn(_owner, _amount);
    }

    /**
     * @notice Burn that amount without reclaiming the reserve, but freeing it up and thereby essentially donating it to the
     * pool share holders. This can make sense in combination with 'coverLoss', i.e. when it is the pool share
     * holders that bear the risk and depending on the outcome they make a profit or a loss.
     *
     * Design rule: Minters calling this method are only allowed to so for tokens amounts they previously minted with
     * the same _reservePPM amount.
     *
     * For example, if someone minted 50 OFD earlier with a 20% reserve requirement (200000 ppm), they got 40 OFD
     * and paid 10 OFD into the reserve. Now they want to repay the debt by burning 50 OFD. When doing so using this
     * method, 50 OFD get burned and on top of that, 10 OFD previously assigned to the minter's reserved are
     * reassigned to the pool share holders.
     */
    function burnWithoutReserve(uint256 amount, uint32 reservePPM) public override minterOnly {
        _burn(msg.sender, amount);
        uint256 reserveReduction = amount * reservePPM;
        if (reserveReduction > minterReserveE6) {
            emit Profit(msg.sender, minterReserveE6 / 1000_000);
            minterReserveE6 = 0; // should never happen, but we want robust behavior in case it does
        } else {
            minterReserveE6 -= reserveReduction;
            emit Profit(msg.sender, reserveReduction / 1000_000);
        }
    }

    /**
     * @notice Burns the provided number of tokens plus whatever reserves are associated with that amount given the reserve
     * requirement. The caller is only allowed to use this method for tokens also minted through the caller with the
     * same _reservePPM amount.
     *
     * Example: the calling contract has previously minted 100 OFD with a reserve ratio of 20% (i.e. 200000 ppm).
     * Now they have 41 OFD that they do not need so they decide to repay that amount. Assuming the reserves are
     * only 90% covered, the call to burnWithReserve will burn the 41 plus 9 from the reserve, reducing the outstanding
     * 'debt' of the caller by 50 OFD in total. This total is returned by the method so the caller knows how much less
     * they owe.
     */
    function burnWithReserve(
        uint256 _amountExcludingReserve,
        uint32 _reservePPM
    ) external override minterOnly returns (uint256) {
        uint256 freedAmount = calculateFreedAmount(_amountExcludingReserve, _reservePPM); // 50 in the example
        minterReserveE6 -= freedAmount * _reservePPM; // reduce reserve requirements by original ratio
        _transfer(address(reserve), msg.sender, freedAmount - _amountExcludingReserve); // collect assigned reserve
        _burn(msg.sender, freedAmount); // burn the rest of the freed amount
        return freedAmount;
    }

    /**
     * @notice Burns the target amount taking the tokens to be burned from the payer and the payer's reserve.
     * Only use this method for tokens also minted by the caller with the same _reservePPM.
     *
     * Example: the calling contract has previously minted 100 OFD with a reserve ratio of 20% (i.e. 200000 ppm).
     * To burn half of that again, the minter calls burnFrom with a target amount of 50 OFD. Assuming that reserves
     * are only 90% covered, this call will deduct 41 OFD from the payer's balance and 9 from the reserve, while
     * reducing the minter reserve by 10.
     */
    function burnFromWithReserve(
        address payer,
        uint256 targetTotalBurnAmount,
        uint32 reservePPM
    ) external override minterOnly returns (uint256) {
        uint256 assigned = calculateAssignedReserve(targetTotalBurnAmount, reservePPM);
        _transfer(address(reserve), payer, assigned); // send reserve to owner
        _burn(payer, targetTotalBurnAmount); // and burn the full amount from the owner's address
        minterReserveE6 -= targetTotalBurnAmount * reservePPM; // reduce reserve requirements by original ratio
        return assigned;
    }

    /**
     * @notice Calculates the reserve attributable to someone who minted the given amount with the given reserve requirement.
     * Under normal circumstances, this is just the reserver requirement multiplied by the amount. However, after a
     * severe loss of capital that burned into the minter's reserve, this can also be less than that.
     */
    function calculateAssignedReserve(uint256 mintedAmount, uint32 _reservePPM) public view returns (uint256) {
        uint256 theoreticalReserve = (_reservePPM * mintedAmount) / 1000000;
        uint256 currentReserve = balanceOf(address(reserve));
        uint256 minterReserve_ = minterReserve();
        if (currentReserve < minterReserve_) {
            // not enough reserves, owner has to take a loss
            return (theoreticalReserve * currentReserve) / minterReserve_;
        } else {
            return theoreticalReserve;
        }
    }

    /**
     * @notice Calculate the amount that is freed when returning amountExcludingReserve given a reserve ratio of reservePPM,
     * taking into account potential losses. Example values in the comments.
     */
    function calculateFreedAmount(
        uint256 amountExcludingReserve /* 41 */,
        uint32 reservePPM /* 20% */
    ) public view returns (uint256) {
        uint256 currentReserve = balanceOf(address(reserve)); // 18, 10% below what we should have
        uint256 minterReserve_ = minterReserve(); // 20
        uint256 adjustedReservePPM = currentReserve < minterReserve_
            ? (reservePPM * currentReserve) / minterReserve_
            : reservePPM; // 18%
        return (1000000 * amountExcludingReserve) / (1000000 - adjustedReservePPM); // 41 / (1-18%) = 50
    }

    /**
     * @notice Notify the OracleFreeDollar that a minter lost economic access to some coins. This does not mean that the coins are
     * literally lost. It just means that some OFD will likely never be repaid and that in order to bring the system
     * back into balance, the lost amount of OFD must be removed from the reserve instead.
     *
     * For example, if a minter printed 1 million OFD for a mortgage and the mortgage turned out to be unsound with
     * the house only yielding 800'000 in the subsequent auction, there is a loss of 200'000 that needs to be covered
     * by the reserve.
     */
    function coverLoss(address source, uint256 _amount) external override minterOnly {
        uint256 reserveLeft = balanceOf(address(reserve));
        if (reserveLeft >= _amount) {
            _transfer(address(reserve), source, _amount);
        } else {
            _transfer(address(reserve), source, reserveLeft);
            _mint(source, _amount - reserveLeft);
        }
        emit Loss(source, _amount);
    }

    function collectProfits(address source, uint256 _amount) external override minterOnly {
        _collectProfits(msg.sender, source, _amount);
    }

    function _collectProfits(address minter, address source, uint256 _amount) internal {
        _transfer(source, address(reserve), _amount);
        emit Profit(minter, _amount);
    }

    /**
     * @notice Returns true if the address is an approved minter.
     */
    function isMinter(address _minter) public view override returns (bool) {
        return minters[_minter] != 0 && block.timestamp >= minters[_minter];
    }

    /**
     * @notice Returns the address of the minter that created this position or null if the provided address is unknown.
     */
    function getPositionParent(address _position) public view override returns (address) {
        return positions[_position];
    }
}

contracts/Position.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './utils/Ownable.sol';
import './utils/MathUtil.sol';

import './interface/IERC20.sol';
import './interface/ILeadrate.sol';
import './interface/IReserve.sol';
import './interface/IOracleFreeDollar.sol';

import './interface/IPosition.sol';

/**
 * @title Position
 * @notice A collateralized minting position.
 */
contract Position is Ownable, IPosition, MathUtil {
	/**
     * @notice Note that this contract is intended to be cloned. All clones will share the same values for
     * the constant and immutable fields, but have their own values for the other fields.
     */

	/**
     * @notice The ofd price per unit of the collateral below which challenges succeed, (36 - collateral.decimals) decimals
     */
	uint256 public price;

	/**
     * @notice Net minted amount, including reserve.
     */
	uint256 public minted;

	/**
     * @notice How much has been minted in total. This variable is only used in the parent position.
     */
	uint256 private totalMinted;

	uint256 public immutable limit;

	/**
     * @notice Amount of the collateral that is currently under a challenge.
     * Used to figure out whether there are pending challenges.
     */
	uint256 public challengedAmount;

	/**
     * @notice Challenge period in seconds.
     */
	uint40 public immutable challengePeriod;

	/**
     * @notice Timestamp when minting can start and the position no longer denied.
     */
	uint40 public immutable start;

	/**
     * @notice End of the latest cooldown. If this is in the future, minting is suspended.
     */
	uint40 public cooldown;

	/**
     * @notice Timestamp of the expiration of the position. After expiration, challenges cannot be averted
     * any more. This is also the basis for fee calculations.
     */
	uint40 public expiration;

	bool private closed;

	/**
     * @notice The original position to help identifying clones.
     */
	address public immutable original;

	/**
     * @notice Pointer to the minting hub.
     */
	address public immutable hub;

	/**
     * @notice The OracleFreeDollar contract.
     */
	IOracleFreeDollar public immutable ofd;

	/**
     * @notice The collateral token.
     */
	IERC20 public immutable override collateral;

	/**
     * @notice Minimum acceptable collateral amount to prevent dust.
     */
	uint256 public immutable override minimumCollateral;

	/**
     * @notice The interest in parts per million per year that is deducted when minting OracleFreeDollars.
     * To be paid upfront.
     */
	uint24 public immutable riskPremiumPPM;

	/**
     * @notice The reserve contribution in parts per million of the minted amount.
     */
	uint24 public immutable reserveContribution;

	event MintingUpdate(uint256 collateral, uint256 price, uint256 minted);
	event PositionDenied(address indexed sender, string message); // emitted if closed by governance

	error InsufficientCollateral(uint256 needed, uint256 available);
	error TooLate();
	error RepaidTooMuch(uint256 excess);
	error LimitExceeded(uint256 tried, uint256 available);
	error ChallengeTooSmall();
	error Expired(uint40 time, uint40 expiration);
	error Alive();
	error Closed();
	error Hot();
	error Challenged();
	error NotHub();
	error NotOriginal();
	error InvalidExpiration();
	error AlreadyInitialized();

	modifier alive() {
		if (block.timestamp >= expiration) revert Expired(uint40(block.timestamp), expiration);
		_;
	}

	// requires that the position has always been backed by a minimal amount of collateral
	modifier backed() {
		if (isClosed()) revert Closed();
		_;
	}

	modifier expired() {
		if (block.timestamp < expiration) revert Alive();
		_;
	}

	modifier noCooldown() {
		if (block.timestamp <= cooldown) revert Hot();
		_;
	}

	modifier noChallenge() {
		if (challengedAmount > 0) revert Challenged();
		_;
	}

	modifier onlyHub() {
		if (msg.sender != address(hub)) revert NotHub();
		_;
	}

	modifier ownerOrRoller() {
		if (msg.sender != address(IHub(hub).roller())) _requireOwner(msg.sender);
		_;
	}

	/**
     * @dev See MintingHub.openPosition
     */
	constructor(address _owner, address _hub, address _ofd, address _collateral,
		uint256 _minCollateral, uint256 _initialLimit,
		uint40 _initPeriod, uint40 _duration, uint40 _challengePeriod,
		uint24 _riskPremiumPPM, uint256 _liqPrice, uint24 _reservePPM) {
		require(_initPeriod >= 3 days); // must be at least three days, recommended to use higher values
		_setOwner(_owner);
		original = address(this);
		hub = _hub;
		ofd = IOracleFreeDollar(_ofd);
		collateral = IERC20(_collateral);
		riskPremiumPPM = _riskPremiumPPM;
		reserveContribution = _reservePPM;
		minimumCollateral = _minCollateral;
		challengePeriod = _challengePeriod;
		start = uint40(block.timestamp) + _initPeriod; // at least three days time to deny the position
		cooldown = start;
		expiration = start + _duration;
		limit = _initialLimit;
		_setPrice(_liqPrice, _initialLimit);
	}

	/**
     * Initialization method for clones.
     * Can only be called once. Should be called immediately after creating the clone.
     */
	function initialize(address parent, uint40 _expiration) external onlyHub {
		if (expiration != 0) revert AlreadyInitialized();
		if (_expiration < block.timestamp || _expiration > Position(original).expiration()) revert InvalidExpiration(); // expiration must not be later than original
		expiration = _expiration;
		price = Position(parent).price();
		_setOwner(hub);
	}

	/**
     * Cloning a position is only allowed if the position is not challenged, not expired and not in cooldown.
     */
	function assertCloneable() external noChallenge noCooldown alive backed {}

	/**
     * Notify the original that some amount has been minted.
     */
	function notifyMint(uint256 mint_) external {
		if (ofd.getPositionParent(msg.sender) != hub) revert NotHub();
		totalMinted += mint_;
	}

	function notifyRepaid(uint256 repaid_) external {
		if (ofd.getPositionParent(msg.sender) != hub) revert NotHub();
		totalMinted -= repaid_;
	}

	/**
     * Should only be called on the original position.
     * Better use 'availableForMinting'.
     */
	function availableForClones() external view returns (uint256) {
		// reserve capacity for the original to the extent the owner provided collateral
		uint256 potential = (_collateralBalance() * price) / ONE_DEC18;
		uint256 unusedPotential = minted > potential ? 0 : potential - minted;
		if (totalMinted + unusedPotential >= limit) {
			return 0;
		} else {
			return limit - totalMinted - unusedPotential;
		}
	}

	/**
     * The amount available for minting in this position family.
     *
     * Does not check if positions are challenged, closed, or under cooldown.
     */
	function availableForMinting() public view returns (uint256) {
		if (address(this) == original) {
			return limit - totalMinted;
		} else {
			return Position(original).availableForClones();
		}
	}

	/**
     * @notice Qualified pool share holders can call this method to immediately expire a freshly proposed position.
     */
	function deny(address[] calldata helpers, string calldata message) external {
		if (block.timestamp >= start) revert TooLate();
		IReserve(ofd.reserve()).checkQualified(msg.sender, helpers);
		_close();
		emit PositionDenied(msg.sender, message);
	}

	/**
     * Closes the position by putting it into eternal cooldown.
     * This allows the users to still withdraw the collateral that is left, but never to mint again.
     */
	function _close() internal {
		closed = true;
	}

	function isClosed() public view returns (bool) {
		return closed;
	}

	/**
     * @notice This is how much the minter can actually use when minting OFD, with the rest being used
     * assigned to the minter reserve or (if applicable) fees.
     */
	function getUsableMint(uint256 totalMint, bool afterFees) public view returns (uint256) {
		if (afterFees) {
			return (totalMint * (1000_000 - reserveContribution - calculateCurrentFee())) / 1000_000;
		} else {
			return (totalMint * (1000_000 - reserveContribution)) / 1000_000;
		}
	}

	/**
     * Returns the corresponding mint amount (disregarding the limit).
     */
	function getMintAmount(uint256 usableMint) external view returns (uint256) {
		return usableMint == 0 ? 0 :(usableMint * 1000_000 - 1) / (1000_000 - reserveContribution - calculateCurrentFee()) + 1;
	}

	/**
     * @notice "All in one" function to adjust the outstanding amount of OFD, the collateral amount,
     * and the price in one transaction.
     */
	function adjust(uint256 newMinted, uint256 newCollateral, uint256 newPrice) external onlyOwner {
		uint256 colbal = _collateralBalance();
		if (newCollateral > colbal) {
			collateral.transferFrom(msg.sender, address(this), newCollateral - colbal);
		}
		// Must be called after collateral deposit, but before withdrawal
		if (newMinted < minted) {
			ofd.burnFromWithReserve(msg.sender, minted - newMinted, reserveContribution);
			_notifyRepaid(minted - newMinted);
		}
		if (newCollateral < colbal) {
			_withdrawCollateral(msg.sender, colbal - newCollateral);
		}
		// Must be called after collateral withdrawal
		if (newMinted > minted) {
			_mint(msg.sender, newMinted - minted, newCollateral);
		}
		if (newPrice != price) {
			_adjustPrice(newPrice);
		}
		emit MintingUpdate(newCollateral, newPrice, newMinted);
	}

	/**
     * @notice Allows the position owner to adjust the liquidation price as long as there is no pending challenge.
     * Lowering the liquidation price can be done with immediate effect, given that there is enough collateral.
     * Increasing the liquidation price triggers a cooldown period of 3 days, during which minting is suspended.
     */
	function adjustPrice(uint256 newPrice) public onlyOwner {
		_adjustPrice(newPrice);
		emit MintingUpdate(_collateralBalance(), price, minted);
	}

	function _adjustPrice(uint256 newPrice) internal noChallenge alive backed {
		if (newPrice > price) {
			_restrictMinting(3 days);
		} else {
			_checkCollateral(_collateralBalance(), newPrice);
		}
		_setPrice(newPrice, minted + availableForMinting());
	}

	function _setPrice(uint256 newPrice, uint256 bounds) internal {
		require(newPrice * minimumCollateral <= bounds * ONE_DEC18); // sanity check
		price = newPrice;
	}

	function _collateralBalance() internal view returns (uint256) {
		return IERC20(collateral).balanceOf(address(this));
	}

	/**
     * @notice Mint OFD as long as there is no open challenge, the position is not subject to a cooldown,
     * and there is sufficient collateral.
     */
	function mint(address target, uint256 amount) public ownerOrRoller {
		uint256 collateralBalance = _collateralBalance();
		_mint(target, amount, collateralBalance);
		emit MintingUpdate(collateralBalance, price, minted);
	}

	/**
     * The applicable upfront fee in ppm when minting more OracleFreeDollars based on the annual interest rate and
     * the expiration of the position.
     */
	function calculateCurrentFee() public view returns (uint24) {
		return calculateFee(expiration);
	}

	/**
     * The applicable interest rate in ppm when minting more OracleFreeDollars.
     * It consists on the globally valid interest plus an individual risk premium.
     */
	function annualInterestPPM() public view returns (uint24) {
		return IHub(hub).rate().currentRatePPM() + riskPremiumPPM;
	}

	/**
     * The fee in ppm when cloning and minting with the given expiration date.
     */
	function calculateFee(uint256 exp) public view returns (uint24) {
		uint256 time = block.timestamp < start ? start : block.timestamp;
		uint256 timePassed = exp - time;
		// Time resolution is in the range of minutes for typical interest rates.
		uint256 feePPM = (timePassed * annualInterestPPM()) / 365 days;
		return uint24(feePPM > 1000000 ? 1000000 : feePPM); // fee cannot exceed 100%
	}

	function _mint(address target, uint256 amount, uint256 collateral_) internal noChallenge noCooldown alive backed {
		if (amount > availableForMinting()) revert LimitExceeded(amount, availableForMinting());
		Position(original).notifyMint(amount);
		ofd.mintWithReserve(target, amount, reserveContribution, calculateCurrentFee());
		minted += amount;
		_checkCollateral(collateral_, price);
	}

	function _restrictMinting(uint40 period) internal {
		uint40 horizon = uint40(block.timestamp) + period;
		if (horizon > cooldown) {
			cooldown = horizon;
		}
	}

	/**
     * @notice Repay some OFD. If too much is repaid, the call fails.
     * It is possible to repay while there are challenges, but the collateral is locked until all is clear again.
     *
     * The repaid amount should fulfill the following equation in order to close the position,
     * i.e. bring the minted amount to 0:
     * minted = amount + ofd.calculateAssignedReserve(amount, reservePPM)
     *
     * Under normal circumstances, this implies:
     * amount = minted * (1000000 - reservePPM)
     *
     * E.g. if minted is 50 and reservePPM is 200000, it is necessary to repay 40 to be able to close the position.
     */
	function repay(uint256 amount) public returns (uint256) {
		IERC20(ofd).transferFrom(msg.sender, address(this), amount);
		uint256 actuallyRepaid = IOracleFreeDollar(ofd).burnWithReserve(amount, reserveContribution);
		_notifyRepaid(actuallyRepaid);
		emit MintingUpdate(_collateralBalance(), price, minted);
		return actuallyRepaid;
	}

	function _notifyRepaid(uint256 amount) internal {
		if (amount > minted) revert RepaidTooMuch(amount - minted);
		Position(original).notifyRepaid(amount);
		minted -= amount;
	}

	/**
     * Force the sale of some collateral after the position is expired.
     *
     * Can only be called by the minting hub and the minting hub is trusted to calculate the price correctly.
     * The proceeds from the sale are first used to repay the outstanding balance and then (if anything is left)
     * it is sent to the owner of the position.
     *
     * Do not allow a forced sale as long as there is an open challenge. Otherwise, a forced sale by the owner
     * himself could remove any incentive to launch challenges shortly before the expiration. (CS-OFD2-001)
     */
	function forceSale(address buyer, uint256 collAmount, uint256 proceeds) external onlyHub expired noChallenge {
		// send collateral to buyer
		uint256 remainingCollateral = _sendCollateral(buyer, collAmount);
		if (minted > 0) {
			uint256 availableReserve = ofd.calculateAssignedReserve(minted, reserveContribution);
			if (proceeds + availableReserve >= minted) {
				// repay everything from the buyer's account
				uint256 returnedReserve = ofd.burnFromWithReserve(buyer, minted, reserveContribution);
				assert(returnedReserve == availableReserve);
				// transfer the remaining purchase price from the buyer to the owner
				ofd.transferFrom(buyer, owner, proceeds + returnedReserve - minted);
				_notifyRepaid(minted);
			} else {
				// we can only repay a part, nothing left to pay to owner
				ofd.transferFrom(buyer, address(this), proceeds);
				if (remainingCollateral == 0) {
					// CS-ZCHF2-002, bad debt should be properly handled. In this case, the proceeds from
					// the forced sale did not suffice to repay the position and there is a loss
					ofd.coverLoss(address(this), minted - proceeds); // more than we need, but returned again on next line
					ofd.burnWithoutReserve(minted, reserveContribution);
					_notifyRepaid(minted);
				} else {
					uint256 repaid = ofd.burnWithReserve(proceeds, reserveContribution);
					_notifyRepaid(repaid);
				}
			}
		} else {
			// wire funds directly to owner
			ofd.transferFrom(buyer, owner, proceeds);
		}
		emit MintingUpdate(_collateralBalance(), price, minted);
	}

	/**
     * @notice Withdraw any ERC20 token that might have ended up on this address.
     * Withdrawing collateral is subject to the same restrictions as withdrawCollateral(...).
     */
	function withdraw(address token, address target, uint256 amount) external onlyOwner {
		if (token == address(collateral)) {
			withdrawCollateral(target, amount);
		} else {
			uint256 balance = _collateralBalance();
			IERC20(token).transfer(target, amount);
			require(balance == _collateralBalance()); // guard against double-entry-point tokens
		}
	}

	/**
     * @notice Withdraw collateral from the position up to the extent that it is still well collateralized afterwards.
     * Not possible as long as there is an open challenge or the contract is subject to a cooldown.
     *
     * Withdrawing collateral below the minimum collateral amount formally closes the position.
     */
	function withdrawCollateral(address target, uint256 amount) public ownerOrRoller {
		uint256 balance = _withdrawCollateral(target, amount);
		emit MintingUpdate(balance, price, minted);
	}

	function _withdrawCollateral(address target, uint256 amount) internal noChallenge returns (uint256) {
		if (block.timestamp <= cooldown) revert Hot();
		uint256 balance = _sendCollateral(target, amount);
		_checkCollateral(balance, price);
		return balance;
	}

	function _sendCollateral(address target, uint256 amount) internal returns (uint256) {
		if (amount > 0) {
			// Some weird tokens fail when trying to transfer 0 amounts
			IERC20(collateral).transfer(target, amount);
		}
		uint256 balance = _collateralBalance();
		if (balance < minimumCollateral) {
			_close();
		}
		return balance;
	}

	/**
     * @notice This invariant must always hold and must always be checked when any of the three
     * variables change in an adverse way.
     */
	function _checkCollateral(uint256 collateralReserve, uint256 atPrice) internal view {
		uint256 relevantCollateral = collateralReserve < minimumCollateral ? 0 : collateralReserve;
		if (relevantCollateral * atPrice < minted * ONE_DEC18) revert InsufficientCollateral(relevantCollateral * atPrice, minted * ONE_DEC18);
	}

	/**
     * @notice Returns the liquidation price and the durations for phase1 and phase2 of the challenge.
     * Both phases are usually of equal duration, but near expiration, phase one is adjusted such that
     * it cannot last beyond the expiration date of the position.
     */
	function challengeData() external view returns (uint256 liqPrice, uint40 phase) {
		return (price, challengePeriod);
	}

	function notifyChallengeStarted(uint256 size) external onlyHub alive {
		// Require minimum size. Collateral balance can be below minimum if it was partially challenged before.
		if (size < minimumCollateral && size < _collateralBalance()) revert ChallengeTooSmall();
		if (size == 0) revert ChallengeTooSmall();
		challengedAmount += size;
	}

	/**
     * @param size   amount of collateral challenged (dec18)
     */
	function notifyChallengeAverted(uint256 size) external onlyHub {
		challengedAmount -= size;

		// Don't allow minter to close the position immediately so challenge can be repeated before
		// the owner has a chance to mint more on an undercollateralized position
		_restrictMinting(1 days);
	}

	/**
     * @notice Notifies the position that a challenge was successful.
     * Triggers the payout of the challenged part of the collateral.
     * Everything else is assumed to be handled by the hub.
     *
     * @param _bidder   address of the bidder that receives the collateral
     * @param _size     amount of the collateral bid for
     * @return (position owner, effective challenge size in OFD, amount to be repaid, reserve ppm)
     */
	function notifyChallengeSucceeded(
		address _bidder,
		uint256 _size
	) external onlyHub returns (address, uint256, uint256, uint32) {
		challengedAmount -= _size;
		uint256 colBal = _collateralBalance();
		if (colBal < _size) {
			_size = colBal;
		}
		uint256 repayment = colBal == 0 ? 0 : (minted * _size) / colBal; // for enormous colBal, this could be rounded to 0, which is ok
		_notifyRepaid(repayment); // we assume the caller takes care of the actual repayment

		// Give time for additional challenges before the owner can mint again. In particular,
		// the owner might have added collateral only seconds before the challenge ended, preventing a close.
		_restrictMinting(3 days);

		uint256 newBalance = _sendCollateral(_bidder, _size); // transfer collateral to the bidder and emit update

		emit MintingUpdate(newBalance, price, minted);

		return (owner, _size, repayment, reserveContribution);
	}
}

interface IHub {
	function rate() external view returns (ILeadrate);
	function roller() external view returns (address);
}

